import React from 'react';
import { Form } from 'react-bootstrap';


/**
* @author
* @function Input
**/

const Input = (props) => {

    let input = null;
    switch (props.type) {
        case 'select':
            input = <Form.Group className="mb-3">
                {props.label && <Form.Label>{props.label}</Form.Label>}
                <select>
                    <option>{props.placeholder}</option>
                    {
                        options.length > 0 ?
                        options.map((option, index) => 
                            <option key={index} value={option.value}>{props.name}</option>
                        ) : null
                    }
                </select>
            </Form.Group>
            break;
        case 'text':
        default:
            input = <Form.Group className="mb-3">
                {props.label && <Form.Label>{props.label}</Form.Label>}
                <Form.Control
                    type={props.type}
                    placeholder={props.placeholder}
                    value={props.value}
                    onChange={props.onChange}
                    {...props}
                />
                <Form.Text className="text-muted">
                    {props.errorMessage}
                </Form.Text>
            </Form.Group>
    }
    return input;

}

export default Input;
