import { categoryConstants } from "../actions/constants"

const initialState = {
    categories: [],
    loading: false,
    error: null
}

const buildNewCategories = (categories, category, id) => {

    let myCategories = [];

    for (let cat of categories) {

        if (cat.parentId && cat.parentId == id) {

            myCategories.push({
                ...cat,
                children: cat.children && cat.children.length > 0 ? buildNewCategory(id, [...cat.children, {
                    _id: category._id,
                    name: category.name,
                    slug: category.slug,
                    parentId: category.parentId,
                    children: category.children
                }], category) : []
            })

        } else {
            myCategories.push({
                ...cat,
                children: cat.children && cat.children.length > 0 ? buildNewCategory(id, cat.children, category) : []
            })
        }

    }

    return myCategories;
}


export default (state = initialState, action) => {
    switch (action.type) {
        case categoryConstants.GET_ALL_CATEGORY_SUCCESS:
            state = {
                ...state,
                categories: action.payload.categories
            }
            break;
        case categoryConstants.ADD_NEW_CATEGORY_REQUEST:
            state = {
                ...state,
                loading: true
            }
            break;
        case categoryConstants.ADD_NEW_CATEGORY_SUCCESS:

            const category = action.payload.category;
            const updatedCategories = buildNewCategories(state.categories, category, category.parentId)
            console.log('updated categories', updatedCategories);
            state = {
                ...state,
                categories: updatedCategories,
                loading: false
            }
            break;
        case categoryConstants.ADD_NEW_CATEGORY_FAILURE:
            state = {
                ...initialState
            }
            break;
    }

    return state;
}
